
public class Uso_de_Operadores {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 7;
		
		int num2 = 5;
		
		float resultado = num1 + num2;
		
		System.out.println(resultado);
		
		resultado = num1 - num2;
		
		System.out.println(resultado);
		
		resultado = num1 * num2;
		
		System.out.println(resultado);
		
		resultado = num1 / num2;
		
		System.out.println(resultado);
		
		resultado = num1 % num2;
		
		System.out.println(resultado);
		
		num1++;
		
		System.out.println(num1);

		num1--;
		
		System.out.println(num1);
		
		resultado +=100;
		
		System.out.println(resultado);
		
		resultado -=100;
		
		System.out.println(resultado);
	}

}
